mapper.select:
select * from list 
where checklist_no = #{checklist_no}
and list_no=#{list_no}

mapper.update:
update list set
list_info = #{list_info}
where checklist_no = #{checklist_no}
and list_no=#{list_no}

mapper.delete:
delete from list 
where checklist_no = #{checklist_no}
and list_no=#{list_no}
